     function displayPopupWnd(dta)
     { 
    	 if(curPopupWnd != null && curPopupWnd.id == dta.id)
    		 return;

    	 // apply the defaults to the data
    	 var defaults = { x:10, y:10, height:40, width:110, leftMargin:10, textLines:[], stroke:"#00214D", fill:"#fff", textFill:mouseOverFill, "font-size": 11} ;
    	 for(var p in defaults){
    	        if(dta[p]=== undefined) dta[p]= defaults[p];
    	    }
    	 if(dta.box === undefined)  // the box is the box that the message has go around
    		 dta.box = {x:dta.x, y:dta.y, width:0, height:0};

    	 // figure out where to place the wnd, so that it doesn't get out of the canvas
    	 var box = dta.box;
    	 var px = box.x < 200 ?  box.x :  box.x + box.width - dta.width;
         var py = box.y < 70 ?  box.y + box.height + 5  : box.y-(dta.height + 5) ;

    	 dta.wnd = R.set();
    	 dta.wnd.push( R.rect(px, py, dta.width, dta.height, 7).attr({stroke:dta.stroke, fill:dta.fill})  );

    	 var line = 0;
    	 for(var i = 0; i < dta.textLines.length; i++)
    	 {
    		var doubleLine =  (dta.textLines[i].indexOf("\n") >0) ? true: false; // if this line contained a linefeed

    		if(i == 0)
    		    var attr = {fill:dta.textFill, "text-anchor":"start", "font-weight":"bold" };
    		else
    		    var attr = {fill:dta.textFill, "text-anchor":"start" };

    		var lineheight = (doubleLine) ? 16: 11;

    		dta.wnd.push(R.text(px +10, py+ lineheight + line * 12, dta.textLines[i]).attr(attr));

    		line++;
    		if (doubleLine) // if this line contained a linefeed
    			line++;
    	 }
    	 curPopupWnd = dta;
     }

     function removePopupWnd(dta)
     {
    	 if(curPopupWnd == null)
    		 return;

    	 if(dta == null || curPopupWnd.id == dta.id)
         {
    		 curPopupWnd.wnd.remove();
    		 curPopupWnd= null;
         }
     }


       function onStateMouseOver( state)
       {
           if(showStatePopups == false)
               return;

    	   outOfState = null;
            if(state == curState)
                 return;

            outOfState = curState;
            onStateMouseOut();

            state.c.animate({fill: mouseOverFill, stroke: "#fff"}, 400);
            state.ct.toFront();
            R.safari();
 
            var b = state.c.getBBox();
            var statecode = state.code+'_' ;
	        displayPopupWnd({box: b, width:190, height:50, id:state.pk, x: b.x, y: b.y,
	        	             textLines: [state.name, 'population ' , 'Key Grant Funding: ' ] });
            curState = state;
       }

       function onStateMouseOut()
       {
           if(showStatePopups == false)
               return;

    	   if(outOfState == null)
               return;

          if(outOfState == curState)
          {
             // statePopup.remove();
        	  removePopupWnd(null);
              curState = null;
          }
          outOfState.c.animate({fill: defaultFill, stroke: "#ccc"}, 400);
          outOfState.ct.toFront();
          R.safari();
          outOfState = null;
       }


    function drawTheMap()
    {
          R = Raphael("paper", paperWidth, paperHeight);
         // waitMessage=  R.image("/COMMON/images/emn/EMNav-ajax-loader.gif", 190, 130, 50, 50);
          waitMessage=  R.text(190, 130, "loading");
          setTimeout("drawTheMap2();",10);
    }

    function drawTheMap2()
    {
        var attrs = {
                fill: defaultFill,
                stroke: defaultStroke, //"#ccc",  // "#666",
                scale: defaultScaleString,
                "stroke-width": 1,
                "stroke-linejoin": "round"
        };

       for(var state in usa)
       {
           (function(st)
           {
               var theHref = "/jurisdictions/" + st.pk;

                st.c = R.path(st.map).attr(attrs).translate(-190, -810);  //.translate(-296, -185); //(-1092, -685);
                if(st.text_x)  // small states have the name elsewhere 
                {
                   var tx = st.text_x * defaultScale;
                   var ty = st.text_y * defaultScale - (defaultScale * ((st.text_y +90)* 0.17));
                   //console.log(st.code+" x: "+tx+" ty: "+ty);
                }
                else
                {  // large states have the name centered
                   var bb =st.c.getBBox();
                   var tx = bb.x+(bb.width/2);
                   var ty =  bb.y+(bb.height/2);
                }
                var radius = (st.text_x ==900) ? 16*defaultScale : 13*defaultScale;
                
                // write the state abbreviations
                st.ctt = R.text(tx, ty, st.code).attr({fill:"#00214D", "font-size": 8});
                st.ct =  R.circle(tx, ty, radius).attr({ stroke:0, fill:"#fff", "opacity": "0.0"});  // this little circle covers the text (in IE the text doesn't get any mouse events)

                // st.c.node.onclick = function () {  alert('click');    };

                st.c.node.onmouseover = function () {
                     onStateMouseOver(st); 
	            };
                st.ct.node.onmouseover = function () {
                	onStateMouseOver(st); 
	            };
                st.ct.node.onmouseout = function () {
                   outOfState = st;
                   setTimeout("onStateMouseOut()", 100);
                };
	            st.c.node.onmouseout = function () {
                   outOfState = st;
                   setTimeout("onStateMouseOut()", 100);
                };
		   	    st.ct.node.onclick = function(event){
		   	    	if(showStatePopups)
		   	            document.location = theHref;
		   	    };
		   	    st.c.node.onclick = function(event){
		   	    	if(showStatePopups)
		   	            document.location = theHref;
		   	    };
          })(usa[state]);

       }
       waitMessage.remove();
   }; //drawTheMap2


